mapper.update:
update shopping_cart
name = #{name}, user_id = #{userId}, dish_id = #{dishId}, set_meal_id = #{setmealId}, dish_Flavor = #{dishFlavor}, number = #{number}, amount = #{amount}, image = #{image}, create_time = #{createTime},
where id = #{id}

mapper.select:
select * from shopping_cart

    and user_id = #{userId}
    and dish_id = #{dishId}
    and set_meal_id = #{setmealId}
    and dish_flavor = #{dishFlavor}

order by create_time desc
